UPDATE B_BOARD
       SET TITLE = #{title},
         CONTENT = #{content}
 	WHERE BB_IDX = #{bb_idx}

UPDATE B_BOARD 
   SET HIT = #{hit} + 1 
 WHERE BB_IDX = #{bb_idx}

UPDATE B_BOARD
   SET HIT = HIT + 1
 WHERE BB_IDX = #{bb_idx}

UPDATE B_BOARD 
   SET GOOD = GOOD + 1 
 WHERE BB_IDX = #{bb_idx}

UPDATE B_BOARD 
   SET BAD = BAD - 1 
 WHERE BB_IDX = #{bb_idx}
